import { useContext, useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api/axios";
import { AuthContext } from "../context/AuthContext";

export default function Verify() {
    const navigate = useNavigate();
    const location = useLocation();
    const { login } = useContext(AuthContext);

    const [email, setEmail] = useState(location.state?.email || "");
    const [code, setCode] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [msg, setMsg] = useState("");

    // if user refreshes the verify page, the state might be lost
    // so we store email in sessionStorage as backup
    useEffect(() => {
        const saved = sessionStorage.getItem("verifyEmail");
        if (saved) setEmail(saved);
    }, []);

    const handleVerify = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email) {
            setError("Missing email. Go back and enter your email again.");
            return;
        }

        if (!code.trim() || code.trim().length !== 6) {
            setError("Please enter the 6-digit code.");
            return;
        }

        setLoading(true);
        try {
            const res = await api.post("/auth/verify-otp", {
                email: email,
                code: code
            });

            // save token + user
            login(res.data);

            setMsg("Verified! Redirecting...");
            navigate("/dashboard");
        } catch (err) {
            setError(err?.response?.data?.message || err?.response?.data?.error || "Verification failed.");
        } finally {
            setLoading(false);
        }
    };

    const handleResend = async () => {
        setError("");
        setMsg("");

        if (!email) {
            setError("Missing email. Go back and enter your email again.");
            return;
        }

        setLoading(true);
        try {
            await api.post("/auth/send-otp", { email: email.trim() });
            setMsg("New code sent. Check your email.");
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to resend code.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ maxWidth: 420, margin: "60px auto", padding: 16 }}>
            <h2>Verify Code</h2>
            <p>
                We sent a 6-digit code to: <b>{email || "—"}</b>
            </p>

            <form onSubmit={handleVerify}>
                <label>6-digit code</label>
                <input
                    type="text"
                    inputMode="numeric"
                    placeholder="123456"
                    value={code}
                    onChange={(e) => setCode(e.target.value)}
                    maxLength={6}
                    style={{ width: "100%", padding: 10, marginTop: 6, marginBottom: 12 }}
                />

                <button
                    type="submit"
                    disabled={loading}
                    style={{ width: "100%", padding: 10, cursor: "pointer" }}
                >
                    {loading ? "Verifying..." : "Verify & Login"}
                </button>
            </form>

            <button
                onClick={handleResend}
                disabled={loading}
                style={{
                    width: "100%",
                    padding: 10,
                    marginTop: 10,
                    cursor: "pointer"
                }}
            >
                Resend code
            </button>

            <button
                onClick={() => navigate("/")}
                disabled={loading}
                style={{
                    width: "100%",
                    padding: 10,
                    marginTop: 10,
                    cursor: "pointer"
                }}
            >
                Back
            </button>

            {msg && <p style={{ marginTop: 12 }}>{msg}</p>}
            {error && <p style={{ marginTop: 12, color: "crimson" }}>{error}</p>}
        </div>
    );
}
