import { useContext, useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api/axios";
import { AuthContext } from "../context/AuthContext";

export default function Verify() {
    const navigate = useNavigate();
    const location = useLocation();
    const { login } = useContext(AuthContext);

    const [email, setEmail] = useState(location.state?.email || "");
    const [code, setCode] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [msg, setMsg] = useState("");

    // if user refreshes the verify page, the state might be lost
    // so we store email in sessionStorage as backup
    useEffect(() => {
        const saved = sessionStorage.getItem("verifyEmail");
        if (saved) setEmail(saved);
    }, []);

    const handleVerify = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email) {
            setError("Missing email. Go back and enter your email again.");
            return;
        }

        if (!code.trim() || code.trim().length !== 6) {
            setError("Please enter the 6-digit code.");
            return;
        }

        setLoading(true);
        try {
            const res = await api.post("/auth/verify-otp", {
                email: email,
                code: code
            });

            // save token + user
            login(res.data);

            setMsg("Verified! Redirecting...");
            navigate("/dashboard");
        } catch (err) {
            setError(err?.response?.data?.message || err?.response?.data?.error || "Verification failed.");
        } finally {
            setLoading(false);
        }
    };

    const handleResend = async () => {
        setError("");
        setMsg("");

        if (!email) {
            setError("Missing email. Go back and enter your email again.");
            return;
        }

        setLoading(true);
        try {
            await api.post("/auth/send-otp", { email: email.trim() });
            setMsg("New code sent. Check your email.");
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to resend code.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-[calc(100vh-0px)] flex items-center justify-center px-4">
            <div className="w-full max-w-md rounded-2xl border border-white/10 bg-white/5 p-6 shadow-xl">
                <h2 className="text-2xl font-semibold">Verify Code</h2>
                <p className="mt-1 text-sm text-slate-300">
                    Enter the 6-digit code sent to your email.
                </p>

                {error && <p className="mt-3 text-sm text-red-300">{error}</p>}
                {msg && <p className="mt-3 text-sm text-emerald-300">{msg}</p>}

                <form onSubmit={handleVerify} className="mt-4 space-y-3">
                    <input
                        type="text"
                        placeholder="Enter 6-digit code"
                        value={code}
                        onChange={(e) => setCode(e.target.value)}
                        className="w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                        required
                    />

                    <button
                        disabled={loading}
                        className="w-full rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
                    >
                        {loading ? "Verifying..." : "Verify"}
                    </button>
                </form>

                <button
                    onClick={handleResend}
                    className="mt-3 w-full rounded-xl border border-white/10 px-4 py-2 text-sm hover:bg-white/5"
                >
                    Resend Code
                </button>
            </div>
        </div>

    );
}
