import { useState } from "react";
import api from "../api/axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
    const [email, setEmail] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");

    const navigate = useNavigate();

    const handleSendOtp = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email.trim()) {
            setError("Please enter your email.");
            return;
        }

        setLoading(true);
        try {
            await api.post("/auth/send-otp", { email: email.trim() });
            setMsg("OTP sent. Check your email.");
            // go to verify page and keep the email
            sessionStorage.setItem("verifyEmail", email.trim().toLowerCase());
            navigate("/verify");

        } catch (err) {
            setError(err?.response?.data?.message || err?.response?.data?.error || "Failed to send OTP.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-[calc(100vh-0px)] flex items-center justify-center px-4">
            <div className="w-full max-w-md rounded-2xl border border-white/10 bg-white/5 p-6 shadow-xl">
                <h2 className="text-2xl font-semibold">Login</h2>
                <p className="mt-1 text-sm text-slate-300">Enter your email to receive a 6-digit code.</p>

                {error && <p className="mt-3 text-sm text-red-300">{error}</p>}
                {msg && <p className="mt-3 text-sm text-emerald-300">{msg}</p>}

                <form onSubmit={handleSendOtp} className="mt-4 space-y-3">
                    <input
                        className="w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                        type="email"
                        placeholder="you@example.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />

                    <button
                        disabled={loading}
                        className="w-full rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
                    >
                        {loading ? "Sending..." : "Send Code"}
                    </button>
                </form>
            </div>
        </div>
    );
}
