import { useEffect, useMemo, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import api from "../api/axios";

function Avatar({ name }) {
  const initials = (name || "DR")
    .split(" ")
    .filter(Boolean)
    .slice(0, 2)
    .map((w) => w[0].toUpperCase())
    .join("");
  return (
    <div className="flex h-14 w-14 items-center justify-center rounded-2xl bg-emerald-600 text-white font-bold text-lg">
      {initials}
    </div>
  );
}

const dayName = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
const fmtTime = (iso) =>
  new Date(iso).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

export default function DoctorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [slotsLoading, setSlotsLoading] = useState(false);

  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  const [date, setDate] = useState(() => {
    const now = new Date();
    const yyyy = now.getFullYear();
    const mm = String(now.getMonth() + 1).padStart(2, "0");
    const dd = String(now.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  });

  const [slots, setSlots] = useState([]);
  const [selectedIso, setSelectedIso] = useState("");
  const [submitting, setSubmitting] = useState(false);

  const fetchDoctor = async () => {
    setError("");
    setLoading(true);
    try {
      const res = await api.get(`/doctors/${id}`);
      setDoctor(res.data);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to load doctor");
    } finally {
      setLoading(false);
    }
  };

  const fetchSlots = async (dateStr) => {
    setError("");
    setMsg("");
    setSlotsLoading(true);
    setSelectedIso("");
    try {
      const res = await api.get(`/doctors/${id}/slots`, { params: { date: dateStr } });
      setSlots(res.data?.slots || []);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to load slots");
      setSlots([]);
    } finally {
      setSlotsLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctor();
    // eslint-disable-next-line
  }, [id]);

  useEffect(() => {
    fetchSlots(date);
    // eslint-disable-next-line
  }, [date, id]);

  const availabilityText = useMemo(() => {
    const a = doctor?.availability;
    if (!a) return "Availability not set";
    const days = (a.days || []).map((d) => dayName[d]).join(", ");
    return `${days} • ${a.start}–${a.end} • ${a.slotMinutes || 30} min slots`;
  }, [doctor]);

  const book = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate(`/login?redirect=/doctors/${id}`);
      return;
    }
    setError("");
    setMsg("");

    if (!selectedIso) {
      setError("Please select a time slot.");
      return;
    }

    setSubmitting(true);
    try {
      await api.post("/appointments", { doctorId: id, date: selectedIso });
      setMsg("Booked (pending). Redirecting...");
      setTimeout(() => navigate("/appointments"), 800);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to book appointment");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="space-y-5">
      <div className="flex flex-wrap items-center justify-between gap-3">
        <Link to="/doctors">
          <button className="rounded-xl border border-emerald-100 bg-white px-4 py-2.5 text-sm hover:bg-emerald-50">
            ← Back to Doctors
          </button>
        </Link>

        <Link to="/appointments">
          <button className="rounded-xl border border-emerald-100 bg-white px-4 py-2.5 text-sm hover:bg-emerald-50">
            My Appointments
          </button>
        </Link>
      </div>

      {error && (
        <div className="rounded-xl border border-red-200 bg-red-50 px-4 py-3 text-sm text-red-700">
          {error}
        </div>
      )}
      {msg && (
        <div className="rounded-xl border border-emerald-200 bg-emerald-50 px-4 py-3 text-sm text-emerald-700">
          {msg}
        </div>
      )}

      {loading && (
        <div className="rounded-2xl border border-emerald-100 bg-white p-6 text-sm text-slate-600">
          Loading doctor...
        </div>
      )}

      {!loading && doctor && (
        <>
          <div className="rounded-3xl border border-emerald-100 bg-white p-6 shadow-sm">
            <div className="flex flex-col md:flex-row gap-6 items-center">

              {/* Avatar */}
              <div className="flex h-28 w-28 items-center justify-center rounded-full bg-emerald-100">
                <Avatar name={doctor.fullName} />
              </div>

              {/* Info */}
              <div className="flex-1 space-y-2 text-center md:text-left">
                <h1 className="text-2xl font-semibold">{doctor.fullName}</h1>
                <span className="inline-flex rounded-full bg-emerald-50 px-3 py-1 text-xs font-medium text-emerald-700 border border-emerald-100">
                  {doctor.department}
                </span>

                <div className="grid gap-2 sm:grid-cols-2 mt-3 text-sm text-slate-700">
                  <div className="rounded-xl border border-emerald-100 px-3 py-2">
                    <b>Experience:</b> {doctor.yearsExperience || 0} years
                  </div>
                  <div className="rounded-xl border border-emerald-100 px-3 py-2">
                    <b>Availability:</b><br />
                    {availabilityText}
                  </div>
                </div>

                {doctor.bio && (
                  <p className="mt-3 text-sm text-slate-600">
                    {doctor.bio}
                  </p>
                )}
              </div>

            </div>
          </div>

        </>
      )}
    </div>
  );
}
