import { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";
import { AuthContext } from "../context/AuthContext";

export default function Verify() {
    const navigate = useNavigate();
    const { login } = useContext(AuthContext);

    const [email, setEmail] = useState(localStorage.getItem("pendingEmail") || "");
    const [code, setCode] = useState("");

    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const verify = async (e) => {
        e.preventDefault();
        setMsg(""); setError("");
        setLoading(true);

        try {
            const res = await api.post("/auth/verify-email", { email, code });
            login(res.data.token, res.data.user);
            localStorage.removeItem("pendingEmail");

            const next = localStorage.getItem("afterLogin");
            if (next) {
                localStorage.removeItem("afterLogin");
                navigate(next);
            } else {
                navigate("/");
            }
        } catch (err) {
            setError(err?.response?.data?.message || "Verification failed");
        } finally {
            setLoading(false);
        }
    };

    const resend = async () => {
        setMsg(""); setError("");
        try {
            await api.post("/auth/resend-verification", { email });
            setMsg("New code sent.");
        } catch (err) {
            setError(err?.response?.data?.message || "Resend failed");
        }
    };

    return (
        <div className="mx-auto max-w-md rounded-2xl bg-white p-6 shadow">
            <h1 className="text-2xl font-semibold text-slate-900">Verify email</h1>

            {msg && <div className="mt-4 rounded-xl bg-emerald-50 p-3 text-sm text-emerald-700">{msg}</div>}
            {error && <div className="mt-4 rounded-xl bg-red-50 p-3 text-sm text-red-700">{error}</div>}

            <form onSubmit={verify} className="mt-5 space-y-3">
                <input className="w-full rounded-xl border p-3" placeholder="Email"
                    value={email} onChange={(e) => setEmail(e.target.value)} />
                <input className="w-full rounded-xl border p-3" placeholder="6-digit code"
                    value={code} onChange={(e) => setCode(e.target.value)} />

                <button disabled={loading}
                    className="w-full rounded-xl bg-emerald-600 px-4 py-3 font-semibold text-white hover:bg-emerald-700 disabled:opacity-60">
                    {loading ? "Verifying..." : "Verify & Continue"}
                </button>
            </form>

            <button onClick={resend} className="mt-3 w-full rounded-xl border px-4 py-3 text-sm">
                Resend code
            </button>
        </div>
    );
}
