import { useContext, useEffect, useState } from "react";
import api from "../api/axios";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";

export default function Appointments() {
    const { user } = useContext(AuthContext);

    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");
    const [rescheduleId, setRescheduleId] = useState(null);
    const [newDateTime, setNewDateTime] = useState("");

    const fetchAppointments = async () => {
        setError("");
        setMsg("");
        setLoading(true);
        try {
            const res = await api.get("/appointments");
            setAppointments(res.data || []);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to load appointments");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchAppointments();
        // eslint-disable-next-line
    }, []);

    const updateStatus = async (id, status) => {
        setError("");
        setMsg("");
        try {
            await api.patch(`/appointments/${id}/status`, { status });
            setMsg(`Appointment ${status}.`);
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to update status");
        }
    };

    const formatDate = (d) => {
        const dt = new Date(d);
        return dt.toLocaleString();
    };

    const cancelAppointment = async (id) => {
        setError("");
        setMsg("");
        try {
            await api.delete(`/appointments/${id}`);
            setMsg("Appointment cancelled.");
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to cancel appointment");
        }
    };

    const reschedule = async () => {
        setError(""); setMsg("");

        if (!rescheduleId) return;
        if (!newDateTime) { setError("Pick a new date/time"); return; }

        try {
            const iso = new Date(newDateTime).toISOString();
            await api.patch(`/appointments/${rescheduleId}/reschedule`, { date: iso });
            setMsg("Appointment rescheduled.");
            setRescheduleId(null);
            setNewDateTime("");
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to reschedule");
        }
    };


    return (
        <div style={{ maxWidth: 900, margin: "60px auto", padding: 16 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h2>Appointments</h2>
                <Link to="/dashboard">
                    <button style={{ padding: 10, cursor: "pointer" }}>Back to Dashboard</button>
                </Link>
            </div>

            {user?.role === "patient" && (
                <div style={{ marginTop: 12 }}>
                    <Link to="/book">
                        <button style={{ padding: 12, cursor: "pointer" }}>Book New Appointment</button>
                    </Link>
                </div>
            )}

            {loading && <p style={{ marginTop: 16 }}>Loading...</p>}
            {msg && <p style={{ marginTop: 16 }}>{msg}</p>}
            {error && <p style={{ marginTop: 16, color: "crimson" }}>{error}</p>}

            {rescheduleId && (
                <div style={{ marginTop: 16, border: "1px solid #ddd", borderRadius: 10, padding: 14 }}>
                    <b>Reschedule Appointment</b>

                    <div style={{ marginTop: 10, display: "grid", gap: 10 }}>
                        <input
                            type="datetime-local"
                            value={newDateTime}
                            onChange={(e) => setNewDateTime(e.target.value)}
                            style={{ padding: 10 }}
                        />

                        <div style={{ display: "flex", gap: 10 }}>
                            <button onClick={reschedule} style={{ padding: 10, cursor: "pointer" }}>
                                Save
                            </button>

                            <button
                                onClick={() => { setRescheduleId(null); setNewDateTime(""); }}
                                style={{ padding: 10, cursor: "pointer" }}
                            >
                                Cancel
                            </button>
                        </div>

                        <small style={{ color: "#555" }}>
                            Note: times snap to 30-minute slots (:00 or :30).
                        </small>
                    </div>
                </div>
            )}


            {!loading && appointments.length === 0 && (
                <p style={{ marginTop: 16 }}>No appointments found.</p>
            )}

            <div style={{ marginTop: 16, display: "grid", gap: 12 }}>
                {appointments.map((a) => {
                    // for patient: doctor is populated
                    // for doctor: patient is populated
                    // for admin: both populated
                    const doctorEmail = a.doctor?.email || a.doctor;
                    const patientEmail = a.patient?.email || a.patient;

                    return (
                        <div
                            key={a._id}
                            style={{ border: "1px solid #ddd", borderRadius: 10, padding: 14 }}
                        >
                            <div style={{ display: "flex", justifyContent: "space-between", gap: 12 }}>
                                <div>
                                    <p style={{ margin: 0 }}>
                                        <b>Date:</b> {formatDate(a.date)}
                                    </p>

                                    <p style={{ margin: "6px 0 0 0" }}>
                                        <b>Status:</b>{" "}
                                        <span
                                            style={{
                                                padding: "2px 8px",
                                                borderRadius: 999,
                                                border: "1px solid #ccc",
                                                display: "inline-block"
                                            }}
                                        >
                                            {a.status}
                                        </span>
                                    </p>

                                    {/* Show relevant counterpart depending on role */}
                                    {user.role === "patient" && (
                                        <p style={{ margin: "6px 0 0 0" }}>
                                            <b>Doctor:</b> {doctorEmail}
                                        </p>
                                    )}

                                    {user.role === "patient" && a.status === "pending" && (
                                        <div style={{ marginTop: 10, display: "flex", gap: 8, flexWrap: "wrap" }}>
                                            <button
                                                onClick={() => { setRescheduleId(a._id); setNewDateTime(""); }}
                                                style={{ padding: 10, cursor: "pointer" }}
                                            >
                                                Reschedule
                                            </button>

                                            <button
                                                onClick={() => cancelAppointment(a._id)}
                                                style={{ padding: 10, cursor: "pointer" }}
                                            >
                                                Cancel Appointment
                                            </button>
                                        </div>
                                    )}

                                    {user.role === "doctor" && (
                                        <p style={{ margin: "6px 0 0 0" }}>
                                            <b>Patient:</b> {patientEmail}
                                        </p>
                                    )}

                                    {user.role === "admin" && (
                                        <>
                                            <p style={{ margin: "6px 0 0 0" }}>
                                                <b>Patient:</b> {patientEmail}
                                            </p>
                                            <p style={{ margin: "6px 0 0 0" }}>
                                                <b>Doctor:</b> {doctorEmail}
                                            </p>
                                        </>
                                    )}
                                </div>

                                {/* Doctor actions */}
                                {user.role === "doctor" && a.status === "pending" && (
                                    <div style={{ display: "flex", flexDirection: "column", gap: 8, minWidth: 140 }}>
                                        <button
                                            onClick={() => updateStatus(a._id, "approved")}
                                            style={{ padding: 10, cursor: "pointer" }}
                                        >
                                            Approve
                                        </button>
                                        <button
                                            onClick={() => updateStatus(a._id, "rejected")}
                                            style={{ padding: 10, cursor: "pointer" }}
                                        >
                                            Reject
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
