import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api/axios";

export default function AdminDoctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);

  const [email, setEmail] = useState("");
  const [fullName, setFullName] = useState("");
  const [department, setDepartment] = useState("");
  const [bio, setBio] = useState("");

  const [submitting, setSubmitting] = useState(false);
  const [msg, setMsg] = useState("");
  const [error, setError] = useState("");

  const fetchDoctors = async () => {
    setError("");
    setMsg("");
    setLoading(true);
    try {
      const res = await api.get("/doctors");
      setDoctors(res.data || []);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to load doctors");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMsg("");

    if (!email.trim() || !fullName.trim() || !department.trim()) {
      setError("Email, full name, and department are required.");
      return;
    }

    setSubmitting(true);
    try {
      await api.post("/doctors", {
        email: email.trim(),
        fullName: fullName.trim(),
        department: department.trim(),
        bio: bio.trim(),
      });

      setMsg("Doctor created/updated successfully.");
      setEmail("");
      setFullName("");
      setDepartment("");
      setBio("");
      fetchDoctors();
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to create/update doctor.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="space-y-5">
      {/* Header */}
      <div className="flex flex-wrap items-center justify-between gap-3">
        <div>
          <h1 className="text-3xl font-semibold">Manage Doctors</h1>
          <p className="mt-1 text-sm text-slate-300">
            Admin panel to create/update doctor profiles.
          </p>
        </div>

        <Link to="/dashboard">
          <button className="rounded-xl border border-white/10 px-4 py-2.5 text-sm hover:bg-white/5">
            Back
          </button>
        </Link>
      </div>

      {/* Alerts */}
      {msg && (
        <div className="rounded-xl border border-emerald-500/30 bg-emerald-500/10 px-4 py-3 text-sm text-emerald-200">
          {msg}
        </div>
      )}
      {error && (
        <div className="rounded-xl border border-red-500/30 bg-red-500/10 px-4 py-3 text-sm text-red-200">
          {error}
        </div>
      )}

      {/* Form */}
      <div className="rounded-2xl border border-white/10 bg-white/5 p-6 shadow">
        <div className="flex flex-col gap-1">
          <h2 className="text-lg font-semibold">Create / Update Doctor</h2>
          <p className="text-sm text-slate-300">
            Note: the doctor should login once (OTP) so a User exists, then you
            can manage their doctor profile here.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="mt-5 grid gap-4">
          <div>
            <label className="text-sm text-slate-300">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="doctor@example.com"
              className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
            />
          </div>

          <div className="grid gap-4 sm:grid-cols-2">
            <div>
              <label className="text-sm text-slate-300">Full Name</label>
              <input
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                placeholder="Dr. Ahmad Ali"
                className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
              />
            </div>

            <div>
              <label className="text-sm text-slate-300">Department</label>
              <input
                type="text"
                value={department}
                onChange={(e) => setDepartment(e.target.value)}
                placeholder="Cardiology"
                className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
              />
            </div>
          </div>

          <div>
            <label className="text-sm text-slate-300">Bio (optional)</label>
            <textarea
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Short description..."
              rows={3}
              className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
            />
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="w-full rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
          >
            {submitting ? "Saving..." : "Save Doctor"}
          </button>
        </form>
      </div>

      {/* List */}
      <div className="space-y-3">
        <div className="flex items-end justify-between gap-3">
          <h2 className="text-lg font-semibold">Doctors List</h2>
          <button
            onClick={fetchDoctors}
            className="rounded-xl border border-white/10 px-3 py-2 text-sm hover:bg-white/5"
          >
            Refresh
          </button>
        </div>

        {loading && (
          <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
            Loading...
          </div>
        )}

        {!loading && doctors.length === 0 && (
          <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
            No doctors yet.
          </div>
        )}

        <div className="grid gap-3">
          {doctors.map((d) => (
            <div
              key={d.id}
              className="rounded-2xl border border-white/10 bg-white/5 p-5 shadow"
            >
              <div className="flex flex-col gap-1">
                <div className="flex flex-wrap items-center justify-between gap-2">
                  <p className="font-semibold">{d.fullName}</p>
                  <span className="rounded-full border border-white/10 bg-white/5 px-3 py-1 text-xs text-slate-200">
                    {d.department}
                  </span>
                </div>

                <p className="text-sm text-slate-300">
                  <span className="text-slate-400">Email:</span>{" "}
                  <span className="text-slate-100">{d.email}</span>
                </p>

                {d.bio && (
                  <p className="text-sm text-slate-300">
                    <span className="text-slate-400">Bio:</span>{" "}
                    <span className="text-slate-100">{d.bio}</span>
                  </p>
                )}

                <p className="mt-2 text-xs text-slate-400">
                  Doctor User ID: {d.id}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
