import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import api from "../api/axios";

export default function Signup() {
    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const submit = async (e) => {
        e.preventDefault();
        setMsg(""); setError("");
        setLoading(true);

        try {
            const res = await api.post("/auth/register", { name, email, password });

            // If admin auto-login returns token
            if (res.data?.token) {
                // handle in AuthContext from Login page usually; for now just go home
                localStorage.setItem("token", res.data.token);
                localStorage.setItem("user", JSON.stringify(res.data.user));
                navigate("/");
                return;
            }

            localStorage.setItem("pendingEmail", res.data.email || email);
            setMsg("Verification code sent. Redirecting...");
            setTimeout(() => navigate("/verify"), 600);
        } catch (err) {
            setError(err?.response?.data?.message || "Signup failed");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="mx-auto max-w-md rounded-2xl bg-white p-6 shadow">
            <h1 className="text-2xl font-semibold text-slate-900">Create account</h1>
            <p className="mt-1 text-sm text-slate-500">Sign up then verify your email.</p>

            {msg && <div className="mt-4 rounded-xl bg-emerald-50 p-3 text-sm text-emerald-700">{msg}</div>}
            {error && <div className="mt-4 rounded-xl bg-red-50 p-3 text-sm text-red-700">{error}</div>}

            <form onSubmit={submit} className="mt-5 space-y-3">
                <input className="w-full rounded-xl border p-3" placeholder="Full name"
                    value={name} onChange={(e) => setName(e.target.value)} />
                <input className="w-full rounded-xl border p-3" placeholder="Email"
                    value={email} onChange={(e) => setEmail(e.target.value)} />
                <input className="w-full rounded-xl border p-3" placeholder="Password" type="password"
                    value={password} onChange={(e) => setPassword(e.target.value)} />

                <button disabled={loading}
                    className="w-full rounded-xl bg-emerald-600 px-4 py-3 font-semibold text-white hover:bg-emerald-700 disabled:opacity-60">
                    {loading ? "Creating..." : "Sign up"}
                </button>
            </form>

            <p className="mt-4 text-sm text-slate-600">
                Already have an account? <Link className="text-emerald-700 underline" to="/login">Login</Link>
            </p>
        </div>
    );
}
