import { useContext, useEffect, useState } from "react";
import api from "../api/axios";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";

export default function Appointments() {
    const { user } = useContext(AuthContext);

    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");
    const [rescheduleId, setRescheduleId] = useState(null);
    const [newDateTime, setNewDateTime] = useState("");

    const fetchAppointments = async () => {
        setError("");
        setMsg("");
        setLoading(true);
        try {
            const res = await api.get("/appointments");
            setAppointments(res.data || []);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to load appointments");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchAppointments();
        // eslint-disable-next-line
    }, []);

    const updateStatus = async (id, status) => {
        setError("");
        setMsg("");
        try {
            await api.patch(`/appointments/${id}/status`, { status });
            setMsg(`Appointment ${status}.`);
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to update status");
        }
    };

    const formatDate = (d) => {
        const dt = new Date(d);
        return dt.toLocaleString();
    };

    const cancelAppointment = async (id) => {
        setError("");
        setMsg("");
        try {
            await api.delete(`/appointments/${id}`);
            setMsg("Appointment cancelled.");
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to cancel appointment");
        }
    };

    const reschedule = async () => {
        setError(""); setMsg("");

        if (!rescheduleId) return;
        if (!newDateTime) { setError("Pick a new date/time"); return; }

        try {
            const iso = new Date(newDateTime).toISOString();
            await api.patch(`/appointments/${rescheduleId}/reschedule`, { date: iso });
            setMsg("Appointment rescheduled.");
            setRescheduleId(null);
            setNewDateTime("");
            fetchAppointments();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to reschedule");
        }
    };


    return (
        <div className="space-y-5">
            {/* Header */}
            <div className="flex flex-wrap items-center justify-between gap-3">
                <div>
                    <h1 className="text-3xl font-semibold">Appointments</h1>
                    <p className="mt-1 text-sm text-slate-300">
                        View and manage your appointments.
                    </p>
                </div>

                {user?.role === "patient" && (
                    <Link to="/book">
                        <button className="rounded-xl bg-blue-600 px-4 py-2.5 text-sm font-semibold hover:bg-blue-700">
                            Book New
                        </button>
                    </Link>
                )}
            </div>

            {/* Alerts */}
            {msg && (
                <div className="rounded-xl border border-emerald-500/30 bg-emerald-500/10 px-4 py-3 text-sm text-emerald-200">
                    {msg}
                </div>
            )}
            {error && (
                <div className="rounded-xl border border-red-500/30 bg-red-500/10 px-4 py-3 text-sm text-red-200">
                    {error}
                </div>
            )}

            {/* Reschedule box */}
            {rescheduleId && (
                <div className="rounded-2xl border border-white/10 bg-white/5 p-5 shadow">
                    <div className="flex items-center justify-between gap-3">
                        <div>
                            <h2 className="text-lg font-semibold">Reschedule Appointment</h2>
                            <p className="mt-1 text-sm text-slate-300">
                                Pick a new date and time (30-min slots).
                            </p>
                        </div>
                        <button
                            onClick={() => {
                                setRescheduleId(null);
                                setNewDateTime("");
                            }}
                            className="rounded-xl border border-white/10 px-3 py-2 text-sm hover:bg-white/5"
                        >
                            Close
                        </button>
                    </div>

                    <div className="mt-4 flex flex-col gap-3 sm:flex-row sm:items-center">
                        <input
                            type="datetime-local"
                            value={newDateTime}
                            onChange={(e) => setNewDateTime(e.target.value)}
                            className="w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                        />

                        <div className="flex gap-2">
                            <button
                                onClick={reschedule}
                                className="rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700"
                            >
                                Save
                            </button>
                            <button
                                onClick={() => {
                                    setRescheduleId(null);
                                    setNewDateTime("");
                                }}
                                className="rounded-xl border border-white/10 px-4 py-3 text-sm hover:bg-white/5"
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Loading / Empty */}
            {loading && (
                <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
                    Loading...
                </div>
            )}

            {!loading && appointments.length === 0 && (
                <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
                    No appointments found.
                </div>
            )}

            {/* List */}
            <div className="grid gap-3">
                {appointments.map((a) => {
                    const doctorEmail = a.doctor?.email || a.doctor;
                    const patientEmail = a.patient?.email || a.patient;

                    const statusClass =
                        a.status === "approved"
                            ? "border-emerald-500/30 bg-emerald-500/10 text-emerald-200"
                            : a.status === "rejected"
                                ? "border-red-500/30 bg-red-500/10 text-red-200"
                                : "border-white/10 bg-white/5 text-slate-200";

                    return (
                        <div
                            key={a._id}
                            className="rounded-2xl border border-white/10 bg-white/5 p-5 shadow"
                        >
                            <div className="flex flex-col gap-4 sm:flex-row sm:items-start sm:justify-between">
                                {/* Left info */}
                                <div className="space-y-2">
                                    <div className="flex flex-wrap items-center gap-2">
                                        <span className="text-sm text-slate-300">Date:</span>
                                        <span className="font-medium">{formatDate(a.date)}</span>
                                    </div>

                                    <div className="flex flex-wrap items-center gap-2">
                                        <span className="text-sm text-slate-300">Status:</span>
                                        <span
                                            className={`rounded-full border px-3 py-1 text-xs ${statusClass}`}
                                        >
                                            {a.status}
                                        </span>
                                    </div>

                                    {user.role === "patient" && (
                                        <div className="text-sm text-slate-500">
                                            Doctor: <span className="text-black">{doctorEmail}</span>
                                        </div>
                                    )}

                                    {user.role === "doctor" && (
                                        <div className="text-sm text-slate-300">
                                            Patient:{" "}
                                            <span className="text-slate-100">{patientEmail}</span>
                                        </div>
                                    )}

                                    {user.role === "admin" && (
                                        <div className="text-sm text-slate-300 space-y-1">
                                            <div>
                                                Patient:{" "}
                                                <span className="text-slate-100">{patientEmail}</span>
                                            </div>
                                            <div>
                                                Doctor:{" "}
                                                <span className="text-slate-100">{doctorEmail}</span>
                                            </div>
                                        </div>
                                    )}

                                    {/* Patient actions */}
                                    {user.role === "patient" && a.status === "pending" && (
                                        <div className="mt-3 flex flex-wrap gap-2">
                                            <button
                                                onClick={() => {
                                                    setRescheduleId(a._id);
                                                    setNewDateTime("");
                                                }}
                                                className="rounded-xl border border-white/10 px-4 py-2 text-sm hover:bg-white/5"
                                            >
                                                Reschedule
                                            </button>
                                            <button
                                                onClick={() => cancelAppointment(a._id)}
                                                className="rounded-xl bg-red-500/15 px-4 py-2 text-sm font-medium text-red-200 ring-1 ring-inset ring-red-500/30 hover:bg-red-500/25"
                                            >
                                                Cancel
                                            </button>
                                        </div>
                                    )}
                                </div>

                                {/* Doctor actions */}
                                {user.role === "doctor" && a.status === "pending" && (
                                    <div className="flex gap-2 sm:flex-col sm:min-w-[160px]">
                                        <button
                                            onClick={() => updateStatus(a._id, "approved")}
                                            className="w-full rounded-xl bg-emerald-600 px-4 py-2.5 text-sm font-semibold hover:bg-emerald-700"
                                        >
                                            Approve
                                        </button>
                                        <button
                                            onClick={() => updateStatus(a._id, "rejected")}
                                            className="w-full rounded-xl bg-red-600 px-4 py-2.5 text-sm font-semibold hover:bg-red-700"
                                        >
                                            Reject
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );

}
