import { createContext, useState } from "react";

export const AuthContext = createContext();

function safeReadUser() {
  const raw = localStorage.getItem("user");

  // raw can be null OR literally "undefined"
  if (!raw || raw === "undefined") return null;

  try {
    return JSON.parse(raw);
  } catch {
    // if it's corrupted, delete it so the app doesn't crash again
    localStorage.removeItem("user");
    return null;
  }
}

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => safeReadUser());

  const login = (data) => {
    // Expecting { token, user }
    if (!data?.token) throw new Error("Missing token from server response");

    localStorage.setItem("token", data.token);

    if (data.user) {
      localStorage.setItem("user", JSON.stringify(data.user));
      setUser(data.user);
    } else {
      // prevent "undefined" being stored ever again
      localStorage.removeItem("user");
      setUser(null);
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
