import { useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Dashboard() {
    const { user, logout } = useContext(AuthContext);
    //const navigate = useNavigate();

    if (!user) return null;

    /*
    const handleLogout = () => {
        logout();
        navigate("/");
    };
    */

    return (
        <div className="space-y-4">
            <div>
                <h1 className="text-3xl font-semibold">Dashboard</h1>
                <p className="text-sm text-slate-300 mt-1">
                    Welcome to Smart Clinic System
                </p>
            </div>

            <div className="rounded-2xl border border-white/10 bg-white/5 p-4 shadow">
                <div className="flex items-center justify-between">
                    <div>
                        <p className="text-sm text-slate-300">Logged in as</p>
                        <p className="font-medium">{user.email}</p>
                    </div>
                    <span className="rounded-full border border-white/10 px-3 py-1 text-xs bg-white/5">
                        {user.role}
                    </span>
                </div>
            </div>

            <div className="grid gap-3 sm:grid-cols-2 md:grid-cols-3">
                <Link to="/appointments">
                    <div className="rounded-xl border border-white/10 bg-white/5 p-4 hover:bg-white/10 cursor-pointer">
                        Appointments
                    </div>
                </Link>

                {user.role === "patient" && (
                    <Link to="/book">
                        <div className="rounded-xl border border-white/10 bg-white/5 p-4 hover:bg-white/10 cursor-pointer">
                            Book Appointment
                        </div>
                    </Link>
                )}

                {user.role === "admin" && (
                    <Link to="/admin/doctors">
                        <div className="rounded-xl border border-white/10 bg-white/5 p-4 hover:bg-white/10 cursor-pointer">
                            Manage Doctors
                        </div>
                    </Link>
                )}
            </div>
        </div>

    );
}
