import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate("/");
    };

    return (
        <div style={{ borderBottom: "1px solid #ddd" }}>
            <div
                style={{
                    maxWidth: 1000,
                    margin: "0 auto",
                    padding: "14px 16px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    gap: 12
                }}
            >
                <Link to="/dashboard" style={{ textDecoration: "none", color: "inherit" }}>
                    <b>Smart Clinic</b>
                </Link>

                {user ? (
                    <div style={{ display: "flex", alignItems: "center", gap: 10, flexWrap: "wrap" }}>
                        <Link to="/dashboard">
                            <button style={{ padding: "8px 10px", cursor: "pointer" }}>Dashboard</button>
                        </Link>

                        <Link to="/appointments">
                            <button style={{ padding: "8px 10px", cursor: "pointer" }}>Appointments</button>
                        </Link>

                        {user.role === "patient" && (
                            <Link to="/book">
                                <button style={{ padding: "8px 10px", cursor: "pointer" }}>Book</button>
                            </Link>
                        )}

                        {user.role === "admin" && (
                            <Link to="/admin/doctors">
                                <button style={{ padding: "8px 10px", cursor: "pointer" }}>Manage Doctors</button>
                            </Link>
                        )}

                        <span style={{ fontSize: 13, color: "#555", marginLeft: 8 }}>
                            {user.email} â€¢ <b>{user.role}</b>
                        </span>

                        <button
                            onClick={handleLogout}
                            style={{ padding: "8px 10px", cursor: "pointer" }}
                        >
                            Logout
                        </button>
                    </div>
                ) : (
                    <div style={{ fontSize: 13, color: "#555" }}>Not logged in</div>
                )}
            </div>
        </div>
    );
}
