import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api/axios";

export default function AdminDoctors() {
    const [doctors, setDoctors] = useState([]);
    const [loading, setLoading] = useState(true);

    const [email, setEmail] = useState("");
    const [fullName, setFullName] = useState("");
    const [department, setDepartment] = useState("");
    const [bio, setBio] = useState("");

    const [submitting, setSubmitting] = useState(false);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");

    const fetchDoctors = async () => {
        setError("");
        setMsg("");
        setLoading(true);
        try {
            const res = await api.get("/doctors");
            setDoctors(res.data || []);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to load doctors");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDoctors();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email.trim() || !fullName.trim() || !department.trim()) {
            setError("Email, full name, and department are required.");
            return;
        }

        setSubmitting(true);
        try {
            await api.post("/doctors", {
                email: email.trim(),
                fullName: fullName.trim(),
                department: department.trim(),
                bio: bio.trim(),
            });

            setMsg("Doctor created/updated successfully.");
            setEmail("");
            setFullName("");
            setDepartment("");
            setBio("");
            fetchDoctors();
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to create/update doctor.");
        } finally {
            setSubmitting(false);
        }
    };

    return (
        <div style={{ maxWidth: 900, margin: "60px auto", padding: 16 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h2>Manage Doctors (Admin)</h2>
                <Link to="/dashboard">
                    <button style={{ padding: 10, cursor: "pointer" }}>Back</button>
                </Link>
            </div>

            {msg && <p style={{ marginTop: 16 }}>{msg}</p>}
            {error && <p style={{ marginTop: 16, color: "crimson" }}>{error}</p>}

            {/* Create/Update Form */}
            <div style={{ marginTop: 16, border: "1px solid #ddd", borderRadius: 10, padding: 14 }}>
                <h3 style={{ marginTop: 0 }}>Create / Update Doctor</h3>
                <p style={{ marginTop: 0, color: "#555" }}>
                    Note: the doctor must login once with OTP so a User account exists, then you can assign them as a doctor.
                </p>

                <form onSubmit={handleSubmit} style={{ display: "grid", gap: 10 }}>
                    <div>
                        <label>Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="doctor@example.com"
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        />
                    </div>

                    <div>
                        <label>Full Name</label>
                        <input
                            type="text"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            placeholder="Dr. Ahmad Ali"
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        />
                    </div>

                    <div>
                        <label>Department</label>
                        <input
                            type="text"
                            value={department}
                            onChange={(e) => setDepartment(e.target.value)}
                            placeholder="Cardiology"
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        />
                    </div>

                    <div>
                        <label>Bio (optional)</label>
                        <textarea
                            value={bio}
                            onChange={(e) => setBio(e.target.value)}
                            placeholder="Short description..."
                            rows={3}
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        />
                    </div>

                    <button type="submit" disabled={submitting} style={{ padding: 12, cursor: "pointer" }}>
                        {submitting ? "Saving..." : "Save Doctor"}
                    </button>
                </form>
            </div>

            {/* Doctors list */}
            <div style={{ marginTop: 18 }}>
                <h3>Doctors List</h3>

                {loading && <p>Loading...</p>}
                {!loading && doctors.length === 0 && <p>No doctors yet.</p>}

                <div style={{ display: "grid", gap: 12 }}>
                    {doctors.map((d) => (
                        <div key={d.id} style={{ border: "1px solid #ddd", borderRadius: 10, padding: 14 }}>
                            <p style={{ margin: 0 }}>
                                <b>{d.fullName}</b> â€” {d.department}
                            </p>
                            <p style={{ margin: "6px 0 0 0" }}>
                                <b>Email:</b> {d.email}
                            </p>
                            {d.bio && (
                                <p style={{ margin: "6px 0 0 0", color: "#444" }}>
                                    <b>Bio:</b> {d.bio}
                                </p>
                            )}
                            <p style={{ margin: "6px 0 0 0", color: "#777", fontSize: 13 }}>
                                Doctor User ID: {d.id}
                            </p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
