import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate("/");
    };

    return (
        <div className="sticky top-0 z-50 border-b border-white/10 bg-slate-950/70 backdrop-blur">
            <div className="mx-auto flex max-w-5xl items-center justify-between gap-3 px-4 py-3">
                <Link to="/dashboard" className="font-semibold tracking-wide">
                    Smart Clinic
                </Link>

                {user ? (
                    <div className="flex flex-wrap items-center gap-2">
                        <Link to="/dashboard" className="rounded-lg border border-white/10 px-3 py-1.5 text-sm hover:bg-white/5">
                            Dashboard
                        </Link>

                        <Link to="/appointments" className="rounded-lg border border-white/10 px-3 py-1.5 text-sm hover:bg-white/5">
                            Appointments
                        </Link>

                        {user.role === "patient" && (
                            <Link to="/book" className="rounded-lg border border-white/10 px-3 py-1.5 text-sm hover:bg-white/5">
                                Book
                            </Link>
                        )}

                        {user.role === "admin" && (
                            <Link to="/admin/doctors" className="rounded-lg border border-white/10 px-3 py-1.5 text-sm hover:bg-white/5">
                                Manage Doctors
                            </Link>
                        )}

                        <span className="hidden sm:inline text-xs text-slate-300">
                            {user.email} â€¢ <span className="font-semibold">{user.role}</span>
                        </span>

                        <button
                            onClick={handleLogout}
                            className="rounded-lg bg-red-500/15 px-3 py-1.5 text-sm font-medium text-red-200 ring-1 ring-inset ring-red-500/30 hover:bg-red-500/25"
                        >
                            Logout
                        </button>
                    </div>
                ) : (
                    <div className="text-xs text-slate-400">Not logged in</div>
                )}
            </div>
        </div>
    );
}
