import { useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Dashboard() {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    if (!user) return null;

    const handleLogout = () => {
        logout();
        navigate("/");
    };

    return (
        <div style={{ maxWidth: 700, margin: "60px auto", padding: 16 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h2>Dashboard</h2>
                <button onClick={handleLogout} style={{ padding: 10, cursor: "pointer" }}>
                    Logout
                </button>
            </div>

            <div style={{ padding: 14, border: "1px solid #ddd", borderRadius: 8, marginTop: 12 }}>
                <p style={{ margin: 0 }}>
                    Logged in as: <b>{user.email}</b>
                </p>
                <p style={{ margin: 0, marginTop: 6 }}>
                    Role: <b>{user.role}</b>
                </p>
            </div>

            <h3 style={{ marginTop: 22 }}>Actions</h3>

            {/* Patient actions */}
            {user.role === "patient" && (
                <div style={{ display: "grid", gap: 10 }}>
                    <Link to="/book">
                        <button style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            Book an Appointment
                        </button>
                    </Link>

                    <Link to="/appointments">
                        <button style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            My Appointments
                        </button>
                    </Link>
                </div>
            )}

            {/* Doctor actions */}
            {user.role === "doctor" && (
                <div style={{ display: "grid", gap: 10 }}>
                    <Link to="/appointments">
                        <button style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            View My Appointments
                        </button>
                    </Link>
                </div>
            )}

            {/* Admin actions */}
            {user.role === "admin" && (
                <div style={{ display: "grid", gap: 10 }}>
                    <Link to="/admin/doctors">
                        <button style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            Manage Doctors
                        </button>
                    </Link>

                    <Link to="/appointments">
                        <button style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            View All Appointments
                        </button>
                    </Link>
                </div>
            )}
        </div>
    );
}
