import { useState } from "react";
import api from "../api/axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
    const [email, setEmail] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");

    const navigate = useNavigate();

    const handleSendOtp = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email.trim()) {
            setError("Please enter your email.");
            return;
        }

        setLoading(true);
        try {
            await api.post("/auth/send-otp", { email: email.trim() });
            setMsg("OTP sent. Check your email.");
            // go to verify page and keep the email
            sessionStorage.setItem("verifyEmail", email.trim().toLowerCase());
            navigate("/verify");

        } catch (err) {
            setError(err?.response?.data?.message || err?.response?.data?.error || "Failed to send OTP.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ maxWidth: 420, margin: "60px auto", padding: 16 }}>
            <h2>Login</h2>
            <p>Enter your email to receive a 6-digit code.</p>

            <form onSubmit={handleSendOtp}>
                <label>Email</label>
                <input
                    type="email"
                    placeholder="you@example.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    style={{ width: "100%", padding: 10, marginTop: 6, marginBottom: 12 }}
                />

                <button
                    type="submit"
                    disabled={loading}
                    style={{ width: "100%", padding: 10, cursor: "pointer" }}
                >
                    {loading ? "Sending..." : "Send Code"}
                </button>
            </form>

            {msg && <p style={{ marginTop: 12 }}>{msg}</p>}
            {error && <p style={{ marginTop: 12, color: "crimson" }}>{error}</p>}
        </div>
    );
}
