import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api/axios";

export default function AdminDoctors() {
    const [doctors, setDoctors] = useState([]);
    const [loading, setLoading] = useState(true);

    const [email, setEmail] = useState("");
    const [fullName, setFullName] = useState("");
    const [department, setDepartment] = useState("");
    const [bio, setBio] = useState("");

    const [submitting, setSubmitting] = useState(false);
    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");

    const [yearsExperience, setYearsExperience] = useState(0);
    const [selectedDays, setSelectedDays] = useState([]);
    const [slotMinutes, setSlotMinutes] = useState(30);

    const [days, setDays] = useState([]);
    const [start, setStart] = useState("");
    const [end, setEnd] = useState("");

    const [editingId, setEditingId] = useState(null);


    const fetchDoctors = async () => {
        setError("");
        setMsg("");
        setLoading(true);
        try {
            const res = await api.get("/doctors");
            setDoctors(res.data || []);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to load doctors");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDoctors();
    }, []);

    const availability = selectedDays.map(day => ({
        day,
        ranges: [
            { start, end }
        ]
    }));

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!email.trim() || !fullName.trim() || !department.trim()) {
            setError("Email, full name, and department are required.");
            return;
        }

        if (selectedDays.length === 0) {
            setError("Choose at least one working day.");
            return;
        }

        if (!start || !end) {
            setError("Choose start and end time.");
            return;
        }

        // IMPORTANT: day values must be 0..6 (Sun=0)
        const availability = selectedDays.map((day) => ({
            day,
            ranges: [{ start, end }],
        }));

        const payload = {
            email: email.trim().toLowerCase(),
            fullName: fullName.trim(),
            department: department.trim(),
            bio: bio.trim(),
            yearsExperience: Number(yearsExperience) || 0,
            availability,
        };

        setSubmitting(true);
        try {
            if (editingId) {
                await api.put(`/doctors/${editingId}`, payload);
                setMsg("Doctor updated.");
            } else {
                await api.post("/doctors", payload);
                setMsg("Doctor created.");
            }

            // reset
            setEditingId(null);
            setEmail("");
            setFullName("");
            setDepartment("");
            setBio("");
            setYearsExperience(0);
            setSelectedDays([]);
            setStart("");
            setEnd("");

            fetchDoctors();
        } catch (err) {
            setError(err?.response?.data?.message || err?.response?.data?.error || "Failed to save doctor.");
        } finally {
            setSubmitting(false);
        }
    };


    const toggleDay = (day) => {
        setSelectedDays(prev =>
            prev.includes(day)
                ? prev.filter(d => d !== day)
                : [...prev, day]
        );
    };

    const uploadAvatar = async (doctorId, file) => {
        setError(""); setMsg("");
        if (!file) return;

        try {
            const form = new FormData();
            form.append("avatar", file);

            const res = await api.patch(`/doctors/${doctorId}/avatar`, form, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setMsg("Avatar uploaded.");
            fetchDoctors(); // refresh list
        } catch (err) {
            setError(err?.response?.data?.message || "Upload failed");
        }
    };

    return (
        <div className="space-y-5">
            {/* Header */}
            <div className="flex flex-wrap items-center justify-between gap-3">
                <div>
                    <h1 className="text-3xl font-semibold">Manage Doctors</h1>
                    <p className="mt-1 text-sm text-slate-300">
                        Admin panel to create/update doctor profiles.
                    </p>
                </div>

                <Link to="/dashboard">
                    <button className="rounded-xl border border-white/10 px-4 py-2.5 text-sm hover:bg-white/5">
                        Back
                    </button>
                </Link>
            </div>

            {/* Alerts */}
            {msg && (
                <div className="rounded-xl border border-emerald-500/30 bg-emerald-500/10 px-4 py-3 text-sm text-emerald-200">
                    {msg}
                </div>
            )}
            {error && (
                <div className="rounded-xl border border-red-500/30 bg-red-500/10 px-4 py-3 text-sm text-red-200">
                    {error}
                </div>
            )}

            {/* Form */}
            <div className="rounded-2xl border border-white/10 bg-white/5 p-6 shadow">
                <div className="flex flex-col gap-1">
                    <h2 className="text-lg font-semibold">Create / Update Doctor</h2>
                    <p className="text-sm text-slate-300">
                        Note: the doctor should login once (OTP) so a User exists, then you
                        can manage their doctor profile here.
                    </p>
                </div>

                <form onSubmit={handleSubmit} className="mt-5 grid gap-4">
                    <div>
                        <label className="text-sm text-slate-300">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="doctor@example.com"
                            className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                        />
                    </div>

                    <div className="grid gap-4 sm:grid-cols-2">
                        <div>
                            <label className="text-sm text-slate-300">Full Name</label>
                            <input
                                type="text"
                                value={fullName}
                                onChange={(e) => setFullName(e.target.value)}
                                placeholder="Dr. Ahmad Ali"
                                className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                            />
                        </div>

                        <div>
                            <label className="text-sm text-slate-300">Department</label>
                            <input
                                type="text"
                                value={department}
                                onChange={(e) => setDepartment(e.target.value)}
                                placeholder="Cardiology"
                                className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                            />
                        </div>
                    </div>

                    <div>
                        <label className="text-sm text-slate-300">Bio (optional)</label>
                        <textarea
                            value={bio}
                            onChange={(e) => setBio(e.target.value)}
                            placeholder="Short description..."
                            rows={3}
                            className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
                        />
                    </div>

                    {/* Years Experience */}
                    <div>
                        <label className="text-sm text-slate-300">Years Experience</label>
                        <input
                            type="number"
                            value={yearsExperience}
                            onChange={(e) => setYearsExperience(e.target.value)}
                            className="mt-2 w-full rounded-xl border px-4 py-3"
                        />
                    </div>

                    {/* Working Days */}
                    <div>
                        <label className="text-sm text-slate-300">Working Days</label>

                        <div className="flex gap-2 mt-2">
                            {[
                                { label: "Mon", val: 1 },
                                { label: "Tue", val: 2 },
                                { label: "Wed", val: 3 },
                                { label: "Thu", val: 4 },
                                { label: "Fri", val: 5 },
                                { label: "Sat", val: 6 },
                                { label: "Sun", val: 0 },
                            ].map(d => (
                                <button
                                    type="button"
                                    key={d.val}
                                    onClick={() => toggleDay(d.val)}
                                    className={`px-3 py-2 rounded-lg border
                                            ${selectedDays.includes(d.val)
                                            ? "bg-green-600 text-white"
                                            : "bg-white text-black"}`}
                                >
                                    {d.label}
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Working Hours */}
                    <div className="grid grid-cols-2 gap-4">
                        <input
                            type="time"
                            value={start}
                            onChange={(e) => setStart(e.target.value)}
                            className="px-4 py-3 rounded-xl border"
                        />

                        <input
                            type="time"
                            value={end}
                            onChange={(e) => setEnd(e.target.value)}
                            className="px-4 py-3 rounded-xl border"
                        />
                    </div>


                    <button
                        type="submit"
                        disabled={submitting}
                        className="w-full rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
                    >
                        {submitting ? "Saving..." : "Save Doctor"}
                    </button>
                </form>
            </div>

            {/* List */}
            <div className="space-y-3">
                <div className="flex items-end justify-between gap-3">
                    <h2 className="text-lg font-semibold">Doctors List</h2>
                    <button
                        onClick={fetchDoctors}
                        className="rounded-xl border border-white/10 px-3 py-2 text-sm hover:bg-white/5"
                    >
                        Refresh
                    </button>
                </div>

                {loading && (
                    <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
                        Loading...
                    </div>
                )}

                {!loading && doctors.length === 0 && (
                    <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
                        No doctors yet.
                    </div>
                )}

                <div className="grid gap-3">
                    {doctors.map((d) => (
                        <div
                            key={d.id}
                            className="rounded-2xl border border-white/10 bg-white/5 p-5 shadow"
                        >
                            <div className="mt-4 flex flex-wrap gap-2">
                                <button
                                    type="button"
                                    onClick={() => {
                                        setEditingId(d.id);
                                        setEmail(d.email);
                                        setFullName(d.fullName || "");
                                        setDepartment(d.department || "");
                                        setBio(d.bio || "");
                                        setYearsExperience(d.yearsExperience || 0);

                                        // prefill days/start/end from first range if exists
                                        const days = (d.availability || []).map(a => a.day);
                                        setSelectedDays(days);

                                        const first = d.availability?.[0]?.ranges?.[0];
                                        setStart(first?.start || "");
                                        setEnd(first?.end || "");
                                    }}
                                    className="rounded-xl bg-emerald-600 px-4 py-2 text-sm font-semibold text-white hover:bg-emerald-700"
                                >
                                    Edit
                                </button>

                                <button
                                    type="button"
                                    onClick={async () => {
                                        setError(""); setMsg("");
                                        try {
                                            await api.delete(`/doctors/${d.id}`);
                                            setMsg("Doctor removed.");
                                            fetchDoctors();
                                        } catch (err) {
                                            setError(err?.response?.data?.message || err?.response?.data?.error || "Remove failed");
                                        }
                                    }}
                                    className="rounded-xl bg-red-600 px-4 py-2 text-sm font-semibold text-white hover:bg-red-700"
                                >
                                    Remove
                                </button>
                            </div>

                            <div className="mt-3 flex items-center gap-3">
                                <input
                                    type="file"
                                    accept="image/*"
                                    onChange={(e) => uploadAvatar(d.id, e.target.files?.[0])}
                                />
                            </div>

                            <div className="flex flex-col gap-1">
                                <div className="flex flex-wrap items-center justify-between gap-2">
                                    <p className="font-semibold">{d.fullName}</p>
                                    <span className="rounded-full border border-white/10 bg-white/5 px-3 py-1 text-xs text-slate-200">
                                        {d.department}
                                    </span>
                                </div>

                                <p className="text-sm text-slate-300">
                                    <span className="text-slate-400">Email:</span>{" "}
                                    <span className="text-black">{d.email}</span>
                                </p>

                                {d.bio && (
                                    <p className="text-sm text-slate-300">
                                        <span className="text-slate-400">Bio:</span>{" "}
                                        <span className="text-slate-100">{d.bio}</span>
                                    </p>
                                )}

                                <p className="mt-2 text-xs text-slate-400">
                                    Doctor User ID: {d.id}
                                </p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
