import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function BookAppointment() {
  const navigate = useNavigate();

  const [doctors, setDoctors] = useState([]);
  const [doctorId, setDoctorId] = useState("");
  const [dateTime, setDateTime] = useState("");

  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  const [msg, setMsg] = useState("");
  const [error, setError] = useState("");

  const fetchDoctors = async () => {
    setError("");
    setMsg("");
    setLoading(true);
    try {
      const res = await api.get("/doctors");
      setDoctors(res.data || []);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to load doctors");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMsg("");

    if (!doctorId) {
      setError("Please choose a doctor.");
      return;
    }
    if (!dateTime) {
      setError("Please choose a date and time.");
      return;
    }

    const isoDate = new Date(dateTime).toISOString();

    if (new Date(isoDate) < new Date()) {
      setError("You can't book an appointment in the past.");
      return;
    }

    setSubmitting(true);
    try {
      await api.post("/appointments", {
        doctorId,
        date: isoDate,
      });

      setMsg("Appointment created (pending). Redirecting...");
      setTimeout(() => navigate("/appointments"), 800);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to create appointment");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="space-y-5">
      {/* Header */}
      <div className="flex flex-wrap items-center justify-between gap-3">
        <div>
          <h1 className="text-3xl font-semibold">Book Appointment</h1>
          <p className="mt-1 text-sm text-slate-300">
            Choose a doctor and reserve a time slot.
          </p>
        </div>

        <Link to="/dashboard">
          <button className="rounded-xl border border-white/10 px-4 py-2.5 text-sm hover:bg-white/5">
            Back
          </button>
        </Link>
      </div>

      {/* Alerts */}
      {msg && (
        <div className="rounded-xl border border-emerald-500/30 bg-emerald-500/10 px-4 py-3 text-sm text-emerald-200">
          {msg}
        </div>
      )}
      {error && (
        <div className="rounded-xl border border-red-500/30 bg-red-500/10 px-4 py-3 text-sm text-red-200">
          {error}
        </div>
      )}

      {/* Loading */}
      {loading && (
        <div className="rounded-2xl border border-white/10 bg-white/5 p-5 text-sm text-slate-300">
          Loading doctors...
        </div>
      )}

      {/* Empty */}
      {!loading && doctors.length === 0 && (
        <div className="rounded-2xl border border-white/10 bg-white/5 p-5">
          <p className="text-sm text-slate-200">No doctors found.</p>
          <p className="mt-2 text-sm text-slate-300">
            Ask the admin to create doctors from{" "}
            <span className="font-semibold">Manage Doctors</span>.
          </p>
        </div>
      )}

      {/* Form */}
      {!loading && doctors.length > 0 && (
        <form
          onSubmit={handleSubmit}
          className="rounded-2xl border border-white/10 bg-white/5 p-6 shadow space-y-4"
        >
          <div>
            <label className="text-sm text-slate-300">Choose Doctor</label>
            <select
              value={doctorId}
              onChange={(e) => setDoctorId(e.target.value)}
              className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
            >
              <option value="">-- Select --</option>
              {doctors.map((d) => (
                <option key={d.id} value={d.id}>
                  {d.fullName} â€” {d.department} ({d.email})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="text-sm text-slate-300">Date & Time</label>
            <input
              type="datetime-local"
              value={dateTime}
              onChange={(e) => setDateTime(e.target.value)}
              className="mt-2 w-full rounded-xl border border-white/10 bg-slate-950/40 px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-blue-500/50"
            />
            <p className="mt-2 text-xs text-slate-400">
              Tip: pick a future time. The server rejects past or already-booked
              slots.
            </p>
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="w-full rounded-xl bg-blue-600 px-4 py-3 text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
          >
            {submitting ? "Booking..." : "Book Appointment"}
          </button>

          <Link to="/appointments" className="block">
            <button
              type="button"
              className="w-full rounded-xl border border-white/10 px-4 py-3 text-sm hover:bg-white/5"
            >
              View My Appointments
            </button>
          </Link>
        </form>
      )}
    </div>
  );
}
