import { useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Dashboard() {
    const { user, logout } = useContext(AuthContext);
    //const navigate = useNavigate();

    //if (!user) return null;

    /*
    const handleLogout = () => {
        logout();
        navigate("/");
    };
    */


    return (
        <div className="bg-green-50 min-h-screen">

            {/* HERO */}
            <div className="max-w-6xl mx-auto px-6 py-20 text-center">

                <h1 className="text-4xl md:text-5xl font-bold text-green-700">
                    Welcome to Smart Clinic
                </h1>

                <p className="mt-4 text-lg text-gray-600">
                    Book appointments with trusted doctors in seconds.
                </p>

                <div className="mt-8 flex justify-center gap-4">
                    <Link to="/doctors">
                        <button className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl shadow">
                            Browse Doctors
                        </button>
                    </Link>

                    <Link to="/login">
                        <button className="border border-green-600 text-green-600 px-6 py-3 rounded-xl hover:bg-green-100">
                            Login to Book
                        </button>
                    </Link>
                </div>

            </div>

            {/* SERVICES */}
            <div className="max-w-6xl mx-auto px-6 py-12 grid md:grid-cols-3 gap-6">

                <div className="bg-white p-6 rounded-xl shadow text-center">
                    <h3 className="text-green-700 font-semibold text-lg">
                        Easy Booking
                    </h3>
                    <p className="mt-2 text-gray-500">
                        Schedule appointments anytime.
                    </p>
                </div>

                <div className="bg-white p-6 rounded-xl shadow text-center">
                    <h3 className="text-green-700 font-semibold text-lg">
                        Qualified Doctors
                    </h3>
                    <p className="mt-2 text-gray-500">
                        Experienced specialists.
                    </p>
                </div>

                <div className="bg-white p-6 rounded-xl shadow text-center">
                    <h3 className="text-green-700 font-semibold text-lg">
                        Secure System
                    </h3>
                    <p className="mt-2 text-gray-500">
                        OTP-based authentication.
                    </p>
                </div>

            </div>

        </div>
    );

}
