import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function BookAppointment() {
    const navigate = useNavigate();

    const [doctors, setDoctors] = useState([]);
    const [doctorId, setDoctorId] = useState("");
    const [dateTime, setDateTime] = useState("");

    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);

    const [msg, setMsg] = useState("");
    const [error, setError] = useState("");

    const fetchDoctors = async () => {
        setError("");
        setMsg("");
        setLoading(true);
        try {
            const res = await api.get("/doctors"); // requires auth
            setDoctors(res.data || []);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to load doctors");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDoctors();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setMsg("");

        if (!doctorId) {
            setError("Please choose a doctor.");
            return;
        }
        if (!dateTime) {
            setError("Please choose a date and time.");
            return;
        }

        // Convert datetime-local string to ISO for backend
        const isoDate = new Date(dateTime).toISOString();

        // Frontend sanity check (backend also checks)
        if (new Date(isoDate) < new Date()) {
            setError("You can't book an appointment in the past.");
            return;
        }

        setSubmitting(true);
        try {
            await api.post("/appointments", {
                doctorId,
                date: isoDate,
            });

            setMsg("Appointment created (pending). Redirecting...");
            setTimeout(() => navigate("/appointments"), 800);
        } catch (err) {
            setError(err?.response?.data?.message || "Failed to create appointment");
        } finally {
            setSubmitting(false);
        }
    };

    return (
        <div style={{ maxWidth: 700, margin: "60px auto", padding: 16 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h2>Book Appointment</h2>
                <Link to="/dashboard">
                    <button style={{ padding: 10, cursor: "pointer" }}>Back</button>
                </Link>
            </div>

            {loading && <p style={{ marginTop: 16 }}>Loading doctors...</p>}
            {msg && <p style={{ marginTop: 16 }}>{msg}</p>}
            {error && <p style={{ marginTop: 16, color: "crimson" }}>{error}</p>}

            {!loading && doctors.length === 0 && (
                <div style={{ marginTop: 16 }}>
                    <p>No doctors found.</p>
                    <p style={{ marginTop: 6 }}>
                        Ask the admin to create doctors (admin route: <code>POST /api/doctors</code>).
                    </p>
                </div>
            )}

            {!loading && doctors.length > 0 && (
                <form onSubmit={handleSubmit} style={{ marginTop: 18, display: "grid", gap: 12 }}>
                    <div>
                        <label>Choose Doctor</label>
                        <select
                            value={doctorId}
                            onChange={(e) => setDoctorId(e.target.value)}
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        >
                            <option value="">-- Select --</option>
                            {doctors.map((d) => (
                                <option key={d.id} value={d.id}>
                                    {d.fullName} â€” {d.department} ({d.email})
                                </option>
                            ))}
                        </select>
                    </div>

                    <div>
                        <label>Date & Time</label>
                        <input
                            type="datetime-local"
                            value={dateTime}
                            onChange={(e) => setDateTime(e.target.value)}
                            style={{ width: "100%", padding: 10, marginTop: 6 }}
                        />
                        <small style={{ display: "block", marginTop: 6, color: "#555" }}>
                            Tip: pick a future time. The server will reject past or already-booked slots.
                        </small>
                    </div>

                    <button
                        type="submit"
                        disabled={submitting}
                        style={{ padding: 12, cursor: "pointer" }}
                    >
                        {submitting ? "Booking..." : "Book Appointment"}
                    </button>

                    <Link to="/appointments" style={{ textDecoration: "none" }}>
                        <button type="button" style={{ width: "100%", padding: 12, cursor: "pointer" }}>
                            View My Appointments
                        </button>
                    </Link>
                </form>
            )}
        </div>
    );
}
